 import React, { useState } from "react";

const RegisterForm = () => {
  const [form, setForm] = useState({ name: "", email: "", password: "" });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    alert("Registrazione in costruzione...");
    // TODO: fetch con POST verso backend
  };

  return (
    <div className="container mt-4">
      <h3>Registrati</h3>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Nome" className="form-control mb-2" onChange={handleChange} />
        <input type="email" name="email" placeholder="Email" className="form-control mb-2" onChange={handleChange} />
        <input type="password" name="password" placeholder="Password" className="form-control mb-2" onChange={handleChange} />
        <button type="submit" className="btn btn-success">Crea Account</button>
      </form>
    </div>
  );
};

export default RegisterForm;
