import React, { useEffect, useState } from "react";
import TripList from "../components/TripList";

const Dashboard = () => {
  const [trips, setTrips] = useState([]);
  const [error, setError] = useState(null);

  const fetchTrips = async () => {
    try {
      const token = localStorage.getItem("token"); // JWT salvato al login
      // console.log("TOKEN:", token);

      const res = await fetch("http://localhost:3001/trips", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!res.ok) throw new Error("Errore nel recupero dei viaggi");
      const data = await res.json();
      setTrips(data);
    } catch (err) {
      console.error(err);
      setError("Impossibile caricare i viaggi");
    }
  };

  useEffect(() => {
    fetchTrips();
  }, []);

  return (
    <div className="container mt-4">
      <h2>Benvenuto nella tua dashboard</h2>
      {error && <p className="text-danger">{error}</p>}
      <TripList trips={trips} />
    </div>
  );
};

export default Dashboard;
